  const [one, setOne] = useState('morning');
  font();
  return (
    <div className="app" >
      <Nav className={dark}>
        <Toggle options={[{value: 'morning', content: 'Morning'}, {value: 'evening', content: 'Evening'}]} selected={one} onChange={setOne}/>
        <Button onClick={()=>{console.log('hi')}}/>
      </Nav>
    </div>
  );
}

export default App;
