import { styled } from './stitches.config';

const StyledButton = styled('button', {
    borderRadius: '9999px',
    padding: '10px 15px',
    fontSize: '16px',
    border: 'none',
    cursor: 'pointer',
    // Variants
    variants: {
        appearance: {
          light: {
            // $ symbol is a token defined in stiches.config.ts
            backgroundColor: '$dark',
            color: '$light',
            '&:hover': {
              backgroundColor: '$light',
              color: '$dark',
            },
          },
          dark: {
            backgroundColor: '$light',
            color: '$dark',
            '&:hover': {
              backgroundColor: '$dark',
              color: '$light',
            },
          },
        },
      },
});

function Buttons() {
    return (
        <div>
            {/* Appearance is defined above as a variant */}
            <StyledButton appearance="dark">Button</StyledButton>
            <StyledButton appearance="light">Button</StyledButton>
        </div>
    );
}

export default Buttons;
