import { styled, globalStyles } from './stitches.config';

const StyledButton = styled('button', {
  // These are styles that apply to all buttons
  borderRadius: '9999px',
  border: 'none',
  cursor: 'pointer',
  outline: 'none',
  fontFamily: 'Test',
  // Variants are subsets of the component
  variants: {
    // You can name these whatever you want
    size: {
      small: {
        padding: '5px 15px',
        fontSize: '14px',
        lineHeight: '20px',
      },
      large: {
        padding: '10px 20px',
        fontSize: '16px',
        lineHeight: '20px',
      },
    },
    type: {
      // And have as many variants as you like
      primary: {
        // Dollar symbols are tokens defined in stiches.config.ts
        backgroundColor: '$foreground',
        color: '$background',
        '&:hover, &:focus': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedStage, $toggleCheckedStage)',
        },
        '&:active': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedSelect, $toggleCheckedSelect)',
        },
      },
      secondary: {
        backgroundColor: '$toggleBase',
        color: '$foreground',
        '&:hover, &:focus': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleStage, $toggleStage)',
        },
        '&:active': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleSelect, $toggleSelect)',
        },
      },
    },
  },
  // Defaults
  defaultVariants: {
    type: 'primary',
    size: 'large',
  }
});

function Buttons() {
    globalStyles();
    return (
        <div>
            {/* These types and sizes are defined above as variants */}
            {/* If you don't pass any, they inherit defaults set above */}
            <StyledButton>Primary large</StyledButton>
            <StyledButton type="secondary" size="large">Secondary large</StyledButton>
            <StyledButton type="primary" size="small">Primary small</StyledButton>
            <StyledButton type="secondary" size="small">Secondary small</StyledButton>
        </div>
    );
}

export default Buttons;
