import { styled } from './stitches.config';
import Icon from './Icon';

const Wrapper = styled('div', {
    position: 'relative',
    display: 'inline-flex',
    alignItems: 'center',
    marginLeft: '-5px',
    marginBottom: '-5px',
    marginTop: '-5px',
    color: '$foreground',
})

const IconWrapper = styled('div', {
    position: 'absolute',
    right: '10px',
    top: '0',
    height: '100%',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
})

const StyledSelect = styled('select', {
    fontFamily: 'inherit',
    fontSize: 'inherit',
    lineHeight: 'inherit',
    background: 'none',
    margin: '0',
    border: 'none',
    borderRadius: '5px',
    color: 'inherit',
    outline: 'none',
    fontWeight: '300',
    appearance: 'none',
    padding: '5px 25px 5px 5px',
    cursor: 'pointer',
    [`&:hover, &:focus, &:hover + ${IconWrapper}, &:focus + ${IconWrapper}`]: {
        opacity: '0.8',
    },
    [`&:active, &:active + ${IconWrapper}`]: {
        opacity: '0.4',
    },
})

interface MessageProps {
    date: string
    setDate: () => void
    dates: string[]
}

const Date = ({date, setDate}: MessageProps) => {
    return(
        <Wrapper>
            <StyledSelect onChange={setDate}>
                <option selected={true} value={date}>{date}</option>
            </StyledSelect>
            <IconWrapper>
                <Icon name="chevron"/>
            </IconWrapper>
        </Wrapper>
    )
}

export default Date;
