import { styled } from './stitches.config';

export const Wrapper = styled('div', {
    display: 'inline-flex',
    background: '$toggleBase',
    borderRadius: '999px',
    padding: '2px',
    width: '200px',
    zIndex: '0',
    position: 'relative',
});

export const Thumb = styled('div', {
    content: '',
    display: 'block',
    width: '98px',
    height: '36px',
    left: '2px',
    top: '2px',
    position: 'absolute',
    borderRadius: '999px',
    zIndex: '1',
    background: '$toggleCheckedBase',
    transition: 'all 0.1s ease',
});

const Radio = styled('input', {
    opacity: '0',
    width: '0',
    height: '0',
    position: 'absolute',
})

function Button() {
    return (
        <Wrapper>
            <Radio type="radio"></Radio>
            <Radio type="radio"></Radio>
        </Wrapper>
    )
}

export default Button;
