import { styled } from './stitches.config';

const StyledButton = styled('button', {
    // The $ is a variable from stitches
    borderRadius: '9999px',
    padding: '10px 15px',
    border: 'none',
    cursor: 'pointer',
    // Variants are subsets of the same component
    variants: {
        appearance: {
          light: {
            backgroundColor: '$dark',
            color: '$light',
            '&:hover': {
              backgroundColor: '$light',
              color: '$dark',
            },
          },
          dark: {
            backgroundColor: '$dark',
            color: '$light',
            '&:hover': {
              backgroundColor: '$light',
              color: '$dark',
            },
          },
        },
      },
});

function Button() {
    return (
        <StyledButton>Button</StyledButton>
    );
}

export default Button;
