import { globalCss } from '@stitches/react';
import { styled, dark } from './stitches.config';
import Buttons from './Buttons';

const Wrapper = styled('div', {
  // Any and every component can have styles
  // These live right alongside the markup which is great
  // No separate CSS files required
  background: '$background',
  padding: '20px'
});

export const globalStyles = globalCss({
  '@font-face': [
    {
      fontFamily: 'test',
      fontWeight: '700',
      src: 'url("./bold.ttf") format("truetype")',
    },
    {
      fontFamily: 'test',
      fontWeight: '300',
      src: 'url("./light.ttf") format("truetype")',
    },
  ],
});

function App() {
  globalStyles();
  return (
    <div className="app" >
      <Wrapper css={{fontFamily: "$Test"}}>
        <Buttons/>
      </Wrapper>
      {/* Themes are passed in as classes, and defined in stitches.config.ts */}
      <Wrapper className={dark}>
        <Buttons/>
      </Wrapper>
    </div>
  );
}

export default App;
