import { styled } from './stitches.config';

const StyledButton = styled('button', {
    borderRadius: '9999px',
    padding: '10px 15px',
    fontSize: '16px',
    border: 'none',
    cursor: 'pointer',
    // Variants
    variants: {
        variant: {
          primary: {
            // $ symbol is a token defined in stiches.config.ts
            backgroundColor: '$foreground',
            color: '$background',
            '&:hover': {
              opacity: '0.8',
            },
          },
          secondary: {
            backgroundColor: '$toggleBase',
            color: '$foreground',
            '&:hover': {
              opacity: '0.8',
            },
          },
        },
      },
});

function Buttons() {
    return (
        <div>
            {/* Appearance is defined above as a variant */}
            <StyledButton variant="primary">Button</StyledButton>
            <StyledButton variant="secondary">Button</StyledButton>
        </div>
    );
}

export default Buttons;
