import { styled, dark } from './stitches.config';
import Buttons from './Buttons';
import './App.css';

const Wrapper = styled('div', {
  [`.${dark}&`]: {
    backgroundColor: '$background',
  },
});

function App() {
  return (
    <div className="app">
      Our cool lectionary site
      <Wrapper>
        <Buttons/>
      </Wrapper>
      {/* Dark theme is passed as class */}
      <Wrapper className={dark}>
        <Buttons/>
      </Wrapper>
    </div>
  );
}

export default App;
