import { styled } from './stitches.config';

const StyledButton = styled('button', {
    // The $ is a variable from stitches
    backgroundColor: '$dark',
    borderRadius: '9999px',
    padding: '10px 15px',
    border: 'none',
    color: '$light',
    cursor: 'pointer',
    '&:hover': {
        backgroundColor: '$medium',
    },
});

function Button() {
    return (
        <StyledButton>Button</StyledButton>
    );
}

export default Button;
