import { useRef, useState, useEffect } from 'react';
import { styled } from './stitches.config';

export const Track = styled('div', {
    display: 'inline-flex',
    background: '$toggleBase',
    borderRadius: '999px',
    padding: '2px',
    width: '200px',
    zIndex: '0',
    position: 'relative',
});

export const Thumb = styled('div', {
    boxSizing: 'border-box',
    content: '',
    display: 'block',
    width: '98px',
    height: '36px',
    left: '2px',
    top: '2px',
    position: 'absolute',
    borderRadius: '999px',
    zIndex: '1',
    background: '$toggleCheckedBase',
    transition: 'all 0.1s ease',
    // TODO
    ':checked:nth-of-type(2) ~ &': {
        left: '100px',
    }
});

const Input = styled('input', {
    opacity: '0',
    width: '0',
    height: '0',
    position: 'absolute',
})


const Label = styled('label', {
    width: '98px',
    borderRadius: '999px',
    //TODO
    padding: '5px 15px',
    height: '36px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    lineHeight: '20px',
    cursor: 'pointer',
    background: 'transparent',
    color: '$foreground',
    transition: 'all 0.1s ease',
    userSelect: 'none',
    position: 'relative',
    zIndex: '2',
    '&:before': {
        content: '',
        display: 'block',
        width: '100%',
        height: '100%',
        left: '0px',
        top: '0px',
        position: 'absolute',
        borderRadius: '999px',
        pointerEvents: 'none',
        zIndex: '-1',
        background: 'transparent',
    },
    '&:hover:before, &:focus:before': {
        background: '$toggleStage',
    },
    '&:active:before': {
        background: '$toggleSelect',
    },
    ':checked + &': {
        color: '$background',
    },
    ':checked + &:hover:before, :checked + &:focus:before': {
        background: '$toggleCheckedStage',
    },
    ':checked + &:active:before': {
        background: '$toggleCheckedSelect',
    },
})

const Item = styled('div', {})

interface ToggleProps {
    onChange: (value: string) => void;
    value: string;
    options: { value: string; content: string }[];
}

const Toggle = ({ options, value, onChange }: ToggleProps) => {
    const ref = useRef("id-" + Math.random());
    const [index, setIndex] = useState<number>(0);

    useEffect(() => {
      const indexOfValue: number = options.map((el) => el.value).indexOf(value);
      setIndex(indexOfValue);
    }, [value, options]);

    return (
        <Track>
            {options.map((item) => {
                const checked = item.value === value;
                return (
                    <Item key={item.value}>
                        <Input
                        id={"for-" + item.value}
                        type="radio"
                        name={ref.current}
                        value={item.value}
                        checked={checked}
                        onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
                            return e.target.checked && onChange(e.target.value);
                        }}
                        />
                        <Label htmlFor={"for-" + item.value}>
                        {item.content}
                        </Label>
                    </Item>
                    );
                })}
            <Thumb></Thumb>
        </Track>
    )
}

export default Toggle;
