// import { styled, dark, font } from './stitches.config';
import Button from './Button';
import Toggle from './Toggle';
import { useState }  from 'react';

// const Wrapper = styled('div', {
//   // Every component can have styles
//   // These live right alongside the markup
//   // No separate CSS files required
//   background: '$background',
//   padding: '20px',
//   fontFamily: 'inter',
// });

const App = () => {
  const [one, setOne] = useState('a');
  const [two, setTwo] = useState('1');
  // This is where we import the font we're using
  // We can then reference it by the name we gave it in stithces.config.ts
  //font();
  return (
    <div className="app" >
      {/* Themes are passed in as classes, and defined in stitches.config.ts */}
      <div >
        <Toggle options={[{value: 'a', content: 'Morning'}, {value: 'b', content: 'Evening'}]} value={one} onChange={setOne}/>
        <Button>Primary large</Button>
        <Button type="secondary" size="large">Secondary large</Button>
        <Button type="primary" size="small">Primary small</Button>
        <Button type="secondary" size="small">Secondary small</Button>
      </div>
      <div>
      <Toggle options={[{value: '1', content: 'Morning'}, {value: '2', content: 'Evening'}]} value={two} onChange={setTwo}/>
        <Button>Primary large</Button>
        <Button type="secondary" size="large">Secondary large</Button>
        <Button type="primary" size="small">Primary small</Button>
        <Button type="secondary" size="small">Secondary small</Button>
      </div>
    </div>
  );
}

export default App;
