import { styled } from './stitches.config';
import Icon from './Icon';

const StyledSelect = styled('select', {
    fontFamily: 'inherit',
    fontSize: 'inherit',
    border: 'none',
    background: 'none',
    padding: '0',
    margin: '0',
    borderRadius: '0',
    color: 'inherit',
    outline: 'none',
    fontWeight: '300',
    appearance: 'none',
})

const Wrapper = styled('div', {
    position: 'relative',
})

interface MessageProps {
    date: any
    setDate: () => void
}

const Message = ({date, setDate}: MessageProps) => {
    return(
        <Wrapper>
            <StyledSelect onChange={setDate}>
                <option selected={true} value={date}>{date}</option>
            </StyledSelect>
            <Icon name="chevron"/>
        </Wrapper>
    )
}

export default Message;
