import React from 'react';
import { styled } from './stitches.config';

export const Button = styled('button', {
  // These are the styles that apply to all buttons
  borderRadius: '9999px',
  border: 'none',
  cursor: 'pointer',
  outline: 'none',
  fontFamily: 'inherit',

  // Variants are subsets of the component
  variants: {

    // You can name variants whatever you want
    shape: {
      rect: {
        padding: '5px 15px',
        fontSize: '14px',
        lineHeight: '20px',
      },
      circ: {
        display: 'flex',
        height: '40px',
        width: '40px',
        padding: '0',
        alignItems: 'center',
        justifyContent: 'center',
      },
    },

    type: {
      primary: {
        backgroundColor: '$foreground',
        color: '$background',
        '&:hover, &:focus': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedStage, $toggleCheckedStage)',
        },
        '&:active': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedSelect, $toggleCheckedSelect)',
        },
      },
      secondary: {
        backgroundColor: '$toggleBase',
        color: '$foreground',
        '&:hover, &:focus': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleStage, $toggleStage)',
        },
        '&:active': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleSelect, $toggleSelect)',
        },
      },
    },
  },
  defaultVariants: {
    type: 'secondary',
    shape: 'rect',
  }
});

export default Button;
