import { css } from '@stitches/react';
import { styled, dark, globalStyles } from './stitches.config';
import Buttons from './Buttons';

const Wrapper = styled('div', {
  // Any and every component can have styles
  // These live right alongside the markup which is great
  // No separate CSS files required
  background: '$background',
  padding: '20px'
});


function App() {
  globalStyles();
  return (
    <div className="app" >
      <Wrapper>
        <Buttons/>
      </Wrapper>
      {/* Themes are passed in as classes, and defined in stitches.config.ts */}
      <Wrapper className={dark}>
        <Buttons/>
      </Wrapper>
    </div>
  );
}

export default App;
