import { styled } from './stitches.config';

const StyledButton = styled('button', {
    borderRadius: '9999px',
    padding: '10px 15px',
    fontSize: '16px',
    border: 'none',
    cursor: 'pointer',
    // Variants are subsets of the component
    variants: {
        // You can name them whatever you want
        type: {
          primary: {
            // $ symbol is a token defined in stiches.config.ts
            backgroundColor: '$foreground',
            color: '$background',
            '&:hover': {
              opacity: '0.8',
            },
          },
          secondary: {
            backgroundColor: '$toggleBase',
            color: '$foreground',
            '&:hover': {
              opacity: '0.8',
            },
          },
        },
        size: {
          small: {
            minHeight: '30px',
          },
          large: {
            minHeight: '50px',
          },
        },
      },
});

function Buttons() {
    return (
        <div>
            {/* Appearance is defined above as a variant */}
            <StyledButton type="primary" size="large">Button</StyledButton>
            <StyledButton type="secondary" size="large">Button</StyledButton>
            <StyledButton type="primary" size="small">Button</StyledButton>
            <StyledButton type="secondary" size="small">Button</StyledButton>
        </div>
    );
}

export default Buttons;
