import { styled, dark, font } from './stitches.config';
import { useState }  from 'react';
import Button from './Button';
import Toggle from './Toggle';
import Nav from './Nav';
import Message from './Message';
import Date from './Date';
import Readings from './Readings'

const Wrapper = styled('div', {
  background: '$background',
  fontFamily: 'inter',
  height: '100%',
  padding: '30px',
});

const readings = [
  {
    text: 'Psalm 111', 
    link: 'https://www.google.com'
  },
  {
    text: '2 Chronicles 29.1-19',
    link: 'https://www.google.com'
  },
  {
    text: 'John 13.21-30', 
    link: 'https://www.google.com'
  }
]

const App = () => {
  const today = 'Wednesday, October 13, 2021'
  const [timeOfDay, setTimeOfDay] = useState('morning');
  const [date, setDate] = useState(today);
  font();
  return (
    <Wrapper className={timeOfDay === 'evening' ? dark : ''}>
      <Nav>
        <Button shape="circ" icon={'arrow'} iconRotate={'90'} onClick={()=>{console.log('hi')}}/>
        <Toggle options={[{value: 'morning', content: 'Morning'}, {value: 'evening', content: 'Evening'}]} selected={timeOfDay} onChange={setTimeOfDay}/>
        <Button shape="circ" icon={'arrow'} iconRotate={'-90'} onClick={()=>{console.log('hi')}}/>
      </Nav>
      <Message time={timeOfDay === 'evening' ? 'Po' : 'Ata'}/>
      <Date date={date} setDate={()=> {console.log('hi')}}/>
      <Readings readings={readings}/>
    </Wrapper>
  );
}

export default App;
