import React from 'react';
import { styled } from './stitches.config';
import type * as Stitches from '@stitches/react';
import Icon from './Icon'

export const StyledButton = styled('button', {
  border: 'none',
  cursor: 'pointer',
  outline: 'none',
  fontFamily: 'inherit',
  variants: {
    shape: {
      rect: {
        padding: '5px 15px',
        fontSize: '14px',
        lineHeight: '20px',
        minHeight: '40px',
        borderRadius: '10px',
      },
      circ: {
        display: 'flex',
        height: '40px',
        width: '40px',
        padding: '0',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '999px',
      },
    },
    style: {
      primary: {
        backgroundColor: '$foreground',
        color: '$background',
        '&:hover, &:focus': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedStage, $toggleCheckedStage)',
        },
        '&:active': {
          backgroundColor: '$foreground',
          backgroundImage: 'linear-gradient($toggleCheckedSelect, $toggleCheckedSelect)',
        },
      },
      secondary: {
        backgroundColor: '$toggleBase',
        color: '$foreground',
        '&:hover, &:focus': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleStage, $toggleStage)',
        },
        '&:active': {
          backgroundColor: '$toggleBase',
          backgroundImage: 'linear-gradient($toggleSelect, $toggleSelect)',
        },
      },
    },
  },
  defaultVariants: {
    style: 'secondary',
    shape: 'rect',
  }
});


type ButtonVariants = Stitches.VariantProps<typeof StyledButton>

interface ButtonProps {
  children?: React.ReactChildren
  onClick: () => void
  shape?: ButtonVariants['shape']
  style?: ButtonVariants['style']
  icon?: string
  rotate?: string
}

const Button = ({children, onClick, shape, style, icon, rotate}: ButtonProps) => {
  const rotation = 'rotate(' + rotate + 'deg)';
  return (
    <StyledButton css={{transform: rotation}} shape={shape} style={style} onClick={onClick}>
      {icon ? <Icon name={icon}/> : null}
      {children}
    </StyledButton>
  )
}

export default Button;
