import { createStitches } from '@stitches/react';

export const {
    styled,
    theme,
    config,
} = createStitches({
    theme: {
        colors: {
            dark: 'black',
            light: 'white',
        },
},
    media: {
        small: '(min-width: 600px)',
        medium: '(min-width: 800px)',
        large: '(min-width: 1000px)',
        xLarge: '(min-width: 1200px)',
    },
    utils: {
        marginX: (value) => ({ marginLeft: value, marginRight: value }),
    },
});
