import { styled, theme } from './stitches.config';

const StyledButton = styled('button', {
    backgroundColor: 'blue',
    borderRadius: '9999px',
    fontSize: '13px',
    padding: '10px 15px',
    border: '0',
    color: theme.white,
    cursor: 'pointer',
    '&:hover': {
        backgroundColor: 'green',
    },
});

function Button() {
    return (
        <StyledButton>Button</StyledButton>
    );
}

export default Button;
