import { styled } from './stitches.config';
import Icon from './Icon';

const StyledSelect = styled('select', {
    fontFamily: 'inherit',
    fontSize: 'inherit',
    lineHeight: 'inherit',
    background: 'none',
    padding: '0',
    margin: '0',
    borderRadius: '0',
    color: 'inherit',
    outline: 'none',
    fontWeight: '300',
    appearance: 'none',
    paddingRight: '20px',
    border: '1px solid blue',
    cursor: 'pointer',
    '&:hover, &:focus, &:hover svg, &:focus svg': {
        opacity: '0.8',
    },
    '&:active, &:active svg': {
        opacity: '0.6',
    },
})

const Wrapper = styled('div', {
    position: 'relative',
    display: 'inline-flex',
    alignItems: 'center',
})

const IconWrapper = styled('div', {
    position: 'absolute',
    right: '5px',
    top: '0',
    pointerEvents: 'none',
})

interface MessageProps {
    date: any
    setDate: () => void
}

const Message = ({date, setDate}: MessageProps) => {
    return(
        <Wrapper>
            <StyledSelect onChange={setDate}>
                <option selected={true} value={date}>{date}</option>
            </StyledSelect>
            <IconWrapper>
                <Icon name="chevron"/>
            </IconWrapper>
        </Wrapper>
    )
}

export default Message;
