import React, { useState } from 'react';
import { styled } from './stitches.config';

export const Radio = styled('div', {
    display: 'inline-flex',
    background: '$toggleBase',
    borderRadius: '999px',
    padding: '2px',
    width: '200px',
    zIndex: '0',
    position: 'relative',
});

export const Thumb = styled('div', {
    content: '',
    display: 'block',
    width: '98px',
    height: '36px',
    left: '2px',
    top: '2px',
    position: 'absolute',
    borderRadius: '999px',
    zIndex: '1',
    background: '$toggleCheckedBase',
    transition: 'all 0.1s ease',
    ':checked:nth-of-type(2) ~ &': {
        left: '100px',
    }
});

const Input = styled('input', {
    opacity: '0',
    width: '0',
    height: '0',
    position: 'absolute',
})


const Label = styled('label', {
    width: '98px',
    borderRadius: '999px',
    padding: '5px 15px',
    height: '36px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    lineHeight: '20px',
    cursor: 'pointer',
    background: 'transparent',
    color: '$foreground',
    transition: 'all 0.1s ease',
    userSelect: 'none',
    position: 'relative',
    zIndex: '2',
    '&:before': {
        content: '',
        display: 'block',
        width: '100%',
        height: '100%',
        left: '0px',
        top: '0px',
        position: 'absolute',
        borderRadius: '999px',
        pointerEvents: 'none',
        zIndex: '-1',
        background: 'transparent',
    },
    '&:hover:before, &:focus:before': {
        background: '$toggleStage',
    },
    '&:active:before': {
        background: '$toggleSelect',
    },
    ':checked + &': {
        color: '$background',
    },
    ':checked + &:hover:before, :checked + &:focus:before': {
        background: '$toggleCheckedStage',
    },
    ':checked + &:active:before': {
        background: '$toggleCheckedSelect',
    },
})

function Toggle() {
    const [time, setTime] = useState('morning');
    return (
        <Radio>
            <Input
                type="radio" 
                name="group"
                id="morning"
                checked={time === 'morning'}
                onChange={ ()=> setTime('morning') }
            />
            <Label>Morning</Label>
            <Input
                type="radio" 
                name="group"
                id="evening"
                checked={time === 'evening'}
                onChange={ ()=> setTime('evening') }
            />
            <Label>Evening</Label>
            <Thumb/>
        </Radio>
    )
}

export default Toggle;
